const collection=document.getElementsByClassName("text-for-circle-in-js"),correctionForSymbols={"А":.5,"Б":0,"В":.5,"Г":0,"Д":0,"Е":1,"Ё":0,"Ж":-1.8,"З":0,"И":-.5,"Й":0,"К":0,"Л":0,"М":0,"Н":-.5,"О":-1,"П":1,"Р":0,"С":0,"Т":0,"У":1,"Ф":0,"Х":0,"Ц":0,"Ч":0,"Ш":0,"Щ":0,"Ъ":0,"Ы":-1,"Ь":1,"Э":0,"Ю":0,"Я":-1};function makeCircleText(t){for(let e=0;e<t.length;e++){var o=t[e].outerText.toUpperCase();const c=360/(o.length+1);t[e].innerHTML=o.split("").map((e,t)=>`<span class="circle-text" style="--rot:${t*c+correctionForSymbols[e]}deg">${e}</span>`).join("")}}makeCircleText(collection);